mapper.select:
SELECT o.order_id AS orderId, u.user_name AS userName, s.schedule_id AS scheduleId, s.departure_time AS departureTime, r.route_id AS routeId, v.vehicle_id AS vehicleId, st1.station_name AS departureStation, st2.station_name AS arrivalStation, s.ticket_price AS ticketPrice FROM carmanage.orders o JOIN carmanage.user u ON o.user_id = u.user_id JOIN carmanage.schedule s ON o.schedule_id = s.schedule_id JOIN carmanage.route r ON s.route_id = r.route_id JOIN carmanage.vehicle v ON s.vehicle_id = v.vehicle_id JOIN carmanage.station st1 ON r.start_station_id = st1.station_id JOIN carmanage.station st2 ON r.end_station_id = st2.station_id
AND u.user_name = #{userName}
AND r.route_id = #{routeId}
AND s.schedule_id = #{scheduleId}
AND v.vehicle_id = #{vehicleId}
AND s.departure_time >= #{departureTime}
AND st1.station_name = #{departureStation}
AND st2.station_name = #{arrivalStation}
